
define(['validSubRegion','validColumn','validRow'],function(validSubRegion, validColumn, validRow){
    return{
        checkValidity:function(grid,row,col,value){
            return validSubRegion.validSubRegion(grid,row,col, value) && validColumn.validColumn(grid,row,col, value) && validRow.validRow(grid,row,col, value);
        }
    };
});